import { useState } from "react";
import '../styles/addBook.css'
const AddBook = () => {
    //title,categories,authors,pageCount,shortDescription,longDescription,thumbnailUrl
    let [title,setTitle] = useState("")
    let [authors,setauthors] = useState("")
    let [categories,setcategories] = useState("")
    let [pageCount,setpageCount] = useState("")
    let [shortDescription,setshortDescription] = useState("")
    let [longDescription,setlongDescription] = useState("")
    let [thumbnailUrl,setthumbnailUrl] = useState("")
    return ( 
        <div className="addbook">
            <h1>Add a Book</h1>
            <div className="form">
                <form action="" className="form-group">
                    <div className="title">
                        <input value={title} onChange={(e)=>setTitle(e.target.value)} className="form-control" type="text" placeholder="title of the book" required/>
                    </div>
                    <div className="authors">
                        <input value={authors} onChange={(e)=>setauthors(e.target.value)} className="form-control" type="text" placeholder="Writer name" required />
                    </div>
                    <div className="categories">
                        <input value={categories} onChange={(e)=>setcategories(e.target.value)} className="form-control" type="text" placeholder="Categories" required/>
                    </div>
                    <div className="pageCount">
                        <input value={pageCount} onChange={(e)=>setpageCount(e.target.value)} className="form-control" type="number" placeholder="page count of the book" required/>
                    </div>
                    <div className="shortDescription">
                        <input value={shortDescription} onChange={(e)=>setshortDescription(e.target.value)} className="form-control" type="text" placeholder="short note" required/>
                        <textarea name="shortDescription" id="12" cols="30" rows="10"></textarea>
                    </div>
                    <div className="longDescription">
                        <input value={longDescription} onChange={(e)=>setlongDescription(e.target.value)} className="form-control" type="text" placeholder="descriptive text" required/>
                    </div>
                    <div className="thumbnailUrl">
                        <input value={thumbnailUrl} onChange={(e)=>setthumbnailUrl(e.target.value)} className="form-control" type="text" placeholder="Image of the book" required />
                    </div>
                    <button className="btn">Add Book</button>
                </form>
            </div>
        </div>
     );
}
 
export default AddBook;
