import { useState } from "react";

const Forms = () => {
    let[email,setEmail]=useState("")
    let[password,setPassword]=useState("")
    let handleSubmit=(e)=>{
        e.preventDefault() // to prevent reloading
        let data={email,password}
        console.log(data);
    }
    return ( 
        <div className="forms">
            <form style={{textAlign:"center"}} action="" onSubmit={handleSubmit}>
                <input type="email" value={email} onChange={(e)=>setEmail(e.target.value)} />
                <br /><br />
                <input type="password"  value={password} onChange={(e)=>setPassword(e.target.value)}/>
                {/* updating the value from user input-e.target.value
                to update it during entering the input -onChange
                adta is always stored inside an object */}

                <br /><br />
                <button>Submit</button>
            </form>
            <h1>Email:{email}</h1>
            <h1>Password:{password}</h1>
        </div>
     );
}
 
export default Forms;
