    let demo=()=>{
        if(value=='Hai')
        setvalue('Hello')
        else
        setvalue('Hai')
    }
    return ( 
        <div className="hooks">
            <h1>{value}</h1>
            <button onClick={demo}>Click me</button>
        </div>
     );
}
 
