import { useState,useEffect } from "react";

import '../styles/bookList.css'
const BookList = () => {
    let[books,setBooks]=useState([])
    useEffect(()=>{
        let fetchData=async()=>{
             let response =await fetch(" http://localhost:1000/books");
              let data= await response.json();
              setBooks(data)
        }
        fetchData()
    },[])
    let handleDelete=(id,title)=>{
        setBooks(books.filter(x=>x.id!=id))
        alert(`${title} has been deleted`)
    }
    return ( 
        <div className="bookList">
            <h1>Book List:{books.length}</h1>
            <div className="book-section">
                {books.map((x)=>(
                    < div className="book-list">
                        <h1>Title:{x.title}</h1>
                        <h3>Authors:{x.authors.toString()}</h3>
                        <h3>Pages:{x.pageCount}</h3>
                        <h3>Category:{x.categories[0]}</h3>
                        <button onClick={()=>handleDelete(x.id,x.title)}>Delete</button>
                    </div>
                ))}
            </div>
        </div>
     );
}
 
export default BookList;
