import { useState } from "react";
const AddBook = () => {
    //title,categories,authors,pageCount,shortDescription,longDescription,thumbnailUrl
    let [title,setTitle] = useState("")
    let [authors,setauthors] = useState("")
    let [categories,setcategories] = useState("")
    let [pageCount,setpageCount] = useState("")
    let [shortDescription,setshortDescription] = useState("")
    let [longDescription,setlongDescription] = useState("")
    let [thumbnailUrl,setthumbnailUrl] = useState("")
    return ( 
        <div className="addbook">
            <h1>Add a Book</h1>
            <div className="form">
                <form action="" className="form-group">
                    <div className="title">
                        <input className="form-control" type="text" placeholder="title of the book" required/>
                    </div>
                    <div className="authors">
                        <input className="form-control" type="text" placeholder="Writer name" required />
                    </div>
                    <div className="categories">
                        <input className="form-control" type="text" placeholder="Categories" required/>
                    </div>
                    <div className="pageCount">
                        <input className="form-control" type="number" placeholder="page count of the book" required/>
                    </div>
                    <div className="shortDescription">
                        <input className="form-control" type="text" placeholder="short note" required/>
                    </div>
                    <div className="longDescription">
                        <input className="form-control" type="text" placeholder="descriptive text" required/>
                    </div>
                    <div className="thumbnailUrl">
                        <input className="form-control" type="text" placeholder="Image of the book" required />
                    </div>
                    <button className="btn">Add Book</button>
                </form>
            </div>
        </div>
     );
}
 
export default AddBook;
