    // let handleClick=()=>{
    //     console.log('You Clicked....!!!!');
    //     alert("u gave an alert msg")
    // }

    let handleClick=(person)=>{
        console.log('hello', person);
    }
    return ( 
        <div className="events">
            <h1>Events</h1>
            <button onClick={()=>handleClick('react')}>Click</button>
        </div>
     );
}
 
export default Events;

