import { useEffect, useState } from 'react';

const Effect = () => {
    //let[data,setValue]=useState("use effect hooked")
    let [value, setValue] = useState([])
    useEffect(() => {
        let fetchData = async () => {
            let response = await fetch(" http://localhost:3000/users ")
            let data = await response.json()
            //console.log( data);
            setValue(data)
        }
        fetchData()
    }, [])
    return (
        <div className="effect">
            <h1>Use effect</h1>
            {value.map((x) => (
                <div>
                    {/*<h1>{data}</h1>
                    <button onClick={()=>setValue("updated")}>Modify</button> */}
                    <h1>Name:{x.name}</h1>
                </div>
            ))}
        </div>
    );
}

export default Effect;


