import { useState,useEffect } from "react";
import '../styles/bookList.css'
const BookList = () => {
    let[books,setBooks]=useState([])
    useEffect(()=>{
        let fetchData=async()=>{
             let response =await fetch(" http://localhost:1000/books");
              let data= await response.json();
              setBooks(data)
        }
        fetchData()
    },[])
    let handleDelete=(id,title)=>{
        setBooks(books.filter(x=>x.id!=id))
        alert(`${title} has been deleted`)
    }
    return ( 
        <div className="bookList">
            <h1>Book List</h1>
            <div className="book-section">
                {books.map((x)=>(
                    <div className="book-list">
                        <div className="title"><h1>Title:{x.title}</h1></div>
                        <div className="authors"><h3>Authors:{x.authors.toString()}</h3></div>
                        <div className="pages"><h3>Pages:{x.pageCount}</h3></div>
                        <div className="category"><h2>Category:{x.categories[0]}</h2></div>
                        <button onClick={()=>handleDelete()}></button>
                    </div>
                ))}
            </div>
        </div>
     );
}
 
export default BookList;
