import { useState } from "react";

const AddUser = () => {
    let [name,setname]=useState("")
    let [age,setage]=useState("")
    let [email,setemail]=useState("")
    let [,setphno]=useState("")

    let handleSubmit = (e) =>{
        e.preventDefault();
        let bookData = {name,age,email,phno}

        //potsing to the server
        fetch('',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(bookData)
        })
        alert('books added sucessfully')
        navigate('/admin/book-list')
    }


    return ( 
        <div className="addUser">
            <h1>Add a new User</h1>
            <div className="form"></div>
            <form action="" onSubmit={handleSubmit}>
            <div className="name">
                <input type="text" value={name} onChange={(e)=>setname(e.target.value)} required placeholder="name of the user" />
            </div>
            <div className="age">
                <input type="number" value={age} onChange={(e)=>setage(e.target.value)} required placeholder="In digits" />
            </div>
            <div className="email">
                <input type="text" value={email} onChange={(e)=>setemail(e.target.value)} required placeholder="Enter your E-mail" min={10} max={10} />
            </div>
            <div className="phno">
                <input type="tel" value={phno} onChange={(e)=>setphno(e.target.value)} required placeholder="0-9 digits"/>
            </div>
            </form>
           
        </div>
     );
}
 
export default AddUser;
