import { useState,useEffect } from "react";
import { Routes } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import '../styles/bookList.css'
const BookList = () => {
    let[books,setBooks]=useState([])
    useEffect(()=>{
        let fetchData=async()=>{
             let response =await fetch('http://localhost:1000/books')
              let data= await response.json();
              setBooks(data)
        }
        fetchData()
    },[books])
    let handleDelete=(id,title)=>{
         fetch(`http://localhost:1000/books/${id}`,{
            method:'DELETE'
         });
         alert(`${title} will be deleted permentaly`)
        // setBooks(books.filter(x=>x.id!=id))
        // alert(`${title} has been deleted`)
    }
    let navigate=useNavigate()
    let handleRead =(id)=>{
        navigate(`/admin/book-list/${id}`)
    }
    return ( 
        <div className="bookList">
            <h1>Book List:{books.length}</h1>
            <div className="book-section">
                {books.map((x)=>(
                    < div className="book-list">
                        <div className="image">
                        <img src={x.thumbnailUrl} alt="" />
                        </div>
                        <div className="info">
                        <h1>Title:{x.title}</h1>
                        <h3>Authors:{x.authors.toString()}</h3>
                        <h3>Pages:{x.pageCount}</h3>
                        <h3>Category:{x.categories.toString()}</h3>
                        <button onClick={()=>handleRead(x.id)}>Read More</button>
                        <button onClick={()=>handleDelete(x.id,x.title)}>Delete</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
     );
}
 
export default BookList;
