import { useState,useEffect } from "react";
import { useLocation } from "react-router-dom";
import '../styles/usersList.css'

const UsersList = () => {
    let location=useLocation()
    let[users,setUsers]=useState([])
    useEffect(()=>{
        let fetchData=async()=>{
             let response =await fetch(`http://localhost:1000/users`)
              let data= await response.json();
              setUsers(data)
        }
        fetchData()
    },[users])
    let handleDelete=(id,name)=>{
        fetch(`http://localhost:1000/books/${id}`,{method: 'DELETE'});
        alert(`${name} will be deleted permenantly`)
        // setUsers(users.filter(x=>x.id!=id))
        // alert(`${name} has been deleted`)
    }
    return ( 
        <div className="usersList">
            <h1>Users list:{users.length}</h1>
            <div className="book-section">
                {users.map((x)=>(
                    <div className="user-list">
                        <h1>Name:{x.name}</h1>
                        <h3>Age:{x.age}</h3>
                        <h3>Email:{x.email}</h3>
                        <h2>Ph.no:{x.phno}</h2>
                        { location.pathname=="/admin/users-List" &&  <button onClick={()=>handleDelete(x.id,x.name)}>Delete</button>}
                    </div>
                ))}
            </div>
        </div>
     );
}
 
export default UsersList;
